from collections import deque
from typing import Deque, Generator, Iterable, Optional

def window(seq:str, n:int=4) -> Generator[Deque[Optional[str]], None, None]:
    it = iter(seq)
    win = deque((next(it, None) for _ in range(n)), maxlen=n)
    yield win
    append = win.append
    for e in it:
        append(e)
        yield win

def get_location_after_marker(input:str, window_size:int) -> int:
    for location, win in enumerate(window(input, window_size)):
        if len(set(win)) == window_size:
            return location + window_size
    return -1

input = ''

part1 = get_location_after_marker(input, 4)
print(f"Day 6, Part 1: {part1}")

part2 = get_location_after_marker(input, 14)
print(f"Day 6, Part 1: {part2}")

def test_day6_part1():
    assert get_location_after_marker('bvwbjplbgvbhsrlpgdmjqwftvncz', 4) == 5
    assert get_location_after_marker('nppdvjthqldpwncqszvftbrmjlhg', 4) == 6
    assert get_location_after_marker('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg',4 ) == 10
    assert get_location_after_marker('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw', 4) == 11

def test_day6_part2():
    assert get_location_after_marker('bvwbjplbgvbhsrlpgdmjqwftvncz', 14) == 23
    assert get_location_after_marker('nppdvjthqldpwncqszvftbrmjlhg', 14) == 23